SELECT \n    sysobjects.name AS TableName,\n    syscolumns.name AS ColumnName,\n    CASE WHEN syscolumns.cdefault > 0 THEN 'Yes' ELSE 'No' END AS HasDefault,\n    CASE WHEN syscolumns.iscomputed = 1 THEN 'Yes' ELSE 'No' END AS IsComputedColumn\nFROM \n    syscolumns\nINNER JOIN \n    sysobjects ON sysobjects.id = syscolumns.id\nWHERE \n    sysobjects.xtype = 'U'\nORDER BY \n    TableName, ColumnName;