-- Enable Service Broker on your database\nALTER DATABASE YourDatabaseName SET ENABLE_BROKER;\nGO\n\n-- Create Message Type\nCREATE MESSAGE TYPE [YourMessageType]\nVALIDATION = WELL_FORMED_XML;\n\n-- Create Contract\nCREATE CONTRACT [YourContract]\n(\n    [YourMessageType] SENT BY INITIATOR\n);\n\n-- Create Queue for messages to be processed\nCREATE QUEUE SubmissionsQueue WITH STATUS = ON, RETENTION = OFF;\n\n-- Create Service using the queue and contract\nCREATE SERVICE [SubmissionsService]\nON QUEUE SubmissionsQueue([YourContract]);\nGO\n\n-- Example of sending a message\nBEGIN DIALOG CONVERSATION @conversation_handle FROM SERVICE [InitiatorService]\nTO SERVICE 'SubmissionsService'\nON CONTRACT [YourContract]\nWITH ENCRYPTION = OFF;\n\nSEND ON CONVERSATION @conversation_handle\nMESSAGE TYPE [YourMessageType] (\n    N'<Message>Text from user</Message>'\n);\nGO\n\n-- Example of receiving a message (this would usually be done by your application)\nBEGIN TRANSACTION;\nRECEIVE TOP(1) conversation_handle, message_type_name, message_body \nFROM SubmissionsQueue;\n\n-- At this point, you can process the message and decide whether to end the conversation (delete) or handle another action\nCOMMIT TRANSACTION;\nGO