INSERT INTO myTable\nSELECT mybigtype(\n           'foo', \n           'bar', \n           'fizzle',\n           CAST(\n               MULTISET(\n                   SELECT REF(S)\n                   FROM anotherTable S\n                   WHERE S.stname = 'dingle'\n                   OR S.stname = 'fangle'\n               ) AS myarrayoflittletypes\n           )\n       ) AS bigtype_obj\nFROM DUAL;