DECLARE\n    TYPE t_cursor IS REF CURSOR;\n    c_names  t_cursor;\n    v_query VARCHAR2(1000);\n    v_name  VARCHAR2(50) := 'John';\n    v_order_column VARCHAR2(50) := 'Name'; -- Ensure this comes from a controlled source to prevent SQL Injection\n    v_sort_direction VARCHAR2(4) := 'ASC';  -- Should be either 'ASC' or 'DESC'\nBEGIN\n    -- Construct the dynamic query safely for order column and direction\n    v_query := 'SELECT * FROM Names WHERE Name = :name ORDER BY ' || \n               v_order_column || ' ' || v_sort_direction;\n\n    -- Open cursor with bind variable\n    OPEN c_names FOR v_query USING v_name;\n    \n    -- Fetch rows from the cursor here if needed\n    \n    CLOSE c_names;\nEXCEPTION\n    WHEN OTHERS THEN\n        CLOSE c_names; -- Ensure cursor is closed in case of exception\n        RAISE;\nEND;