CREATE TABLE Movies (\n    MovieID INT PRIMARY KEY,\n    Title VARCHAR(100),\n    Director VARCHAR(100) WITH (COLUMN_SET FOR ALL_SPARSE_COLUMNS),\n    LeadingMale VARCHAR(100) WITH (COLUMN_SET FOR ALL_SPARSE_COLUMNS),\n    LeadingFemale VARCHAR(100) WITH (COLUMN_SET FOR ALL_SPARSE_COLUMNS),\n    Villain VARCHAR(100) WITH (COLUMN_SET FOR ALL_SPARSE_COLUMNS),\n    MetaData NVARCHAR(MAX)\n);\n\nINSERT INTO Movies VALUES\n(1, 'Green Lantern', 'Kubrick', 'Robert Redford', 'Miley Cyrus', 'Hugh Grant', '{ "Director": { "Source": "CHUD", "Journalist": "Sarah" }, ... }');