-- Stored Procedure to insert data into a temporary table\nCREATE PROCEDURE InsertDataIntoTempTable\nAS\nBEGIN\n    CREATE TABLE #TempTable (ID INT, Name NVARCHAR(100));\n    INSERT INTO #TempTable (ID, Name) VALUES (1, 'John Doe');\nEND;\n\n-- Stored Procedure to use the temporary table created by another procedure\nCREATE PROCEDURE UseTempTableData\nAS\nBEGIN\n    -- This procedure must be called after InsertDataIntoTempTable in the same connection\n    SELECT * FROM #TempTable;\nEND;