-- Stored Procedure to return data as XML\nCREATE PROCEDURE GetXmlData\nAS\nBEGIN\n    SELECT ID, Name FOR XML AUTO;\nEND;\n\n-- Stored Procedure using the XML output from another procedure\nDECLARE @XmlResult XML;\nSET @XmlResult = (EXEC GetXmlData FOR XML PATH(''));\nSELECT Tbl.Col.value('@ID[1]', 'INT') AS ID,\n       Tbl.Col.value('@Name[1]', 'NVARCHAR(100)') AS Name\nFROM @XmlResult.nodes('/row') AS Tbl(Col);