-- Stored Procedure Implementation\nCREATE PROCEDURE spMyStoredProcedure\n    @List varchar(255)\nAS\nBEGIN\n    SELECT ID\n    FROM (\n        SELECT ID,\n               CASE WHEN col1 = 1 THEN 1 ELSE NULL END AS C1,\n               CASE WHEN col2 = 1 THEN 2 ELSE NULL END AS C2,\n               CASE WHEN col3 = 1 THEN 3 ELSE NULL END AS C3,\n               CASE WHEN col4 = 1 THEN 4 ELSE NULL END AS C4\n        FROM YourTableName\n    ) T\n    CROSS APPLY dbo.SplitString(@List, ',') s\n    WHERE CAST(s.value AS INT) IN (T.C1, T.C2, T.C3, T.C4)\nEND