SELECT DISTINCT s.name AS SchemaName, o.name AS ObjectName\nFROM sys.objects o\nINNER JOIN sys.schemas s ON o.schema_id = s.schema_id\nWHERE (o.type_desc LIKE 'USER_TABLE' OR o.type_desc LIKE 'SQL_STORED_PROCEDURE')\nAND EXISTS (\n    SELECT *\n    FROM sys.sql_modules sm\n    WHERE sm.object_id = o.object_id\n    AND sm.definition LIKE '%your_search_text%' -- Replace with your search text\n)