WITH RECURSIVE parent_chain AS (\n    -- Start with the initial child node\n    SELECT id, name, parentid, value\n    FROM example\n    WHERE id = :child_id  -- Replace :child_id with the actual child ID\n\n    UNION ALL\n\n    -- Recursively join to find parents\n    SELECT p.id, p.name, p.parentid, p.value\n    FROM example p\n    JOIN parent_chain c ON p.id = c.parentid\n)\n-- Select the root of the hierarchy (the topmost parent)\nSELECT id, name, parentid, value\nFROM parent_chain\nWHERE parentid IS NULL;