-- Table for users\nCREATE TABLE Users (\n    id_user INT PRIMARY KEY AUTO_INCREMENT,\n    username VARCHAR(255) NOT NULL UNIQUE,\n    password_hash VARCHAR(255) NOT NULL\n);\n\n-- Table for groups\nCREATE TABLE Groups (\n    id_group INT PRIMARY KEY AUTO_INCREMENT,\n    name VARCHAR(255) NOT NULL UNIQUE\n);\n\n-- Table for file metadata\nCREATE TABLE Files (\n    id_file INT PRIMARY KEY AUTO_INCREMENT,\n    title VARCHAR(255),\n    reference VARCHAR(255),\n    abstract TEXT,\n    id_owner INT,\n    id_group INT,\n    status ENUM('draft', 'final') DEFAULT 'draft',\n    FOREIGN KEY (id_owner) REFERENCES Users(id_user),\n    FOREIGN KEY (id_group) REFERENCES Groups(id_group)\n);\n\n-- Table for file versions\nCREATE TABLE Versions (\n    id_version INT PRIMARY KEY AUTO_INCREMENT,\n    id_file INT,\n    version_number INT NOT NULL,\n    date_submitted DATETIME DEFAULT CURRENT_TIMESTAMP,\n    path_to_file VARCHAR(255) NOT NULL, -- Store relative paths to physical files\n    is_final BOOLEAN DEFAULT FALSE,\n    FOREIGN KEY (id_file) REFERENCES Files(id_file)\n);