CREATE OR REPLACE FUNCTION fnc_getReadableSize(p_blob IN BLOB)\nRETURN VARCHAR2 IS\n  v_size NUMBER;\n  v_readable_size NUMBER;\n  v_unit VARCHAR2(2);\nBEGIN\n  -- Get the size of the BLOB\n  v_size := DBMS_LOB.GETLENGTH(p_blob);\n\n  -- Determine the appropriate unit and calculate the size\n  IF v_size >= POWER(1024, 3) THEN\n    v_readable_size := v_size / POWER(1024, 3);\n    v_unit := ' GB';\n  ELSIF v_size >= POWER(1024, 2) THEN\n    v_readable_size := v_size / POWER(1024, 2);\n    v_unit := ' MB';\n  ELSIF v_size >= 1024 THEN\n    v_readable_size := v_size / 1024;\n    v_unit := ' KB';\n  ELSE\n    v_readable_size := v_size;\n    v_unit := ' B';\n  END IF;\n\n  -- Return the size formatted to one decimal place with unit\n  RETURN ROUND(v_readable_size, 1) || v_unit;\nEND fnc_getReadableSize;\n/