CREATE TRIGGER trg_YourTable_Delete\nON YourSchema.YourTable\nFOR DELETE\nAS\nBEGIN\n    DECLARE @CurrentUserID UNIQUEIDENTIFIER;\n    \n    -- Assuming application sets this context info\n    SET @CurrentUserID = (SELECT CONVERT(UNIQUEIDENTIFIER, value) \n                           FROM sys.dm_exec_session_context_info\n                           WHERE name = 'UserID');\n\n    INSERT INTO AuditTable (UserID, Action, ActionTime)\n    VALUES (@CurrentUserID, 'DELETE', GETDATE());\nEND;