CREATE TABLE SpaceQuadrant (\n    ID INT PRIMARY KEY AUTO_INCREMENT,\n    EntryName VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE SpaceCoordinate (\n    X INT NOT NULL,\n    Y INT NOT NULL,\n    Z INT NOT NULL,\n    PRIMARY KEY (X, Y, Z)\n);\n\nCREATE TABLE SpaceObject (\n    ID INT PRIMARY KEY AUTO_INCREMENT,\n    Name VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE QuadrantToObjectMapping (\n    SpaceQuadrantID INT NOT NULL,\n    CoordinatesX INT NOT NULL,\n    CoordinatesY INT NOT NULL,\n    CoordinatesZ INT NOT NULL,\n    SpaceObjectID INT NOT NULL,\n    PRIMARY KEY (SpaceQuadrantID, CoordinatesX, CoordinatesY, CoordinatesZ),\n    FOREIGN KEY (SpaceQuadrantID) REFERENCES SpaceQuadrant(ID),\n    FOREIGN KEY (CoordinatesX, CoordinatesY, CoordinatesZ) REFERENCES SpaceCoordinate(X, Y, Z),\n    FOREIGN KEY (SpaceObjectID) REFERENCES SpaceObject(ID)\n);