CREATE TABLE EntitiesA (\n    EntityAID INT PRIMARY KEY,\n    -- other attributes...\n);\n\nCREATE TABLE EntitiesB (\n    EntityBID INT PRIMARY KEY,\n    -- other attributes...\n);\n\nCREATE TABLE JunctionTable (\n    EntityAID INT NOT NULL,\n    EntityBID INT NOT NULL,\n    ValidStart DATETIME2(7) NOT NULL DEFAULT SYSUTCDATETIME(),\n    ValidEnd DATETIME2(7) NOT NULL DEFAULT '9999-12-31 23:59:59.9999999',\n    PRIMARY KEY (EntityAID, EntityBID, ValidStart),\n    FOREIGN KEY (EntityAID) REFERENCES EntitiesA(EntityAID),\n    FOREIGN KEY (EntityBID) REFERENCES EntitiesB(EntityBID)\n);