-- Creating a table with years stored as integers\nCREATE TABLE HistoricalEvents (\n    EventID int PRIMARY KEY,\n    StartDate int, -- YYYYMMDD format\n    EndDate int   -- YYYYMMDD format\n);\n\n-- Inserting some historical events (pre-1753)\nINSERT INTO HistoricalEvents (EventID, StartDate, EndDate) VALUES\n(1, 16920419, 16920922), -- Salem Witch Trials\n(2, 10661014, 10661014); -- Battle of Hastings\n\n-- Query to find events between two dates (e.g., all events in the year 1692)\nSELECT *\nFROM HistoricalEvents\nWHERE StartDate >= 16920000 AND EndDate <= 16929999;\n\n-- Example of converting stored integer date back to a more readable format using string functions and manipulation\nSELECT \n    EventID,\n    CAST(SUBSTRING(CAST(StartDate AS varchar), 1, 4) + '-' +\n         SUBSTRING(CAST(StartDate AS varchar), 5, 2) + '-' +\n         SUBSTRING(CAST(StartDate AS varchar), 7, 2) AS DATE) AS StartDateFormatted,\n    CAST(SUBSTRING(CAST(EndDate AS varchar), 1, 4) + '-' +\n         SUBSTRING(CAST(EndDate AS varchar), 5, 2) + '-' +\n         SUBSTRING(CAST(EndDate AS varchar), 7, 2) AS DATE) AS EndDateFormatted\nFROM HistoricalEvents;