SELECT Items.Name, tblBooks.AuthorLastName, tblBooks.AuthorFirstName, Recommended.SKU\nFROM Items \nINNER JOIN (\n    SELECT TOP 10 Recommended.ProductCode, Recommended.SKU\n    FROM Recommended \n    INNER JOIN Stock ON Recommended.ProductCode = Stock.ProductCode\n    AND Stock.StatusCode = 1\n    WHERE Recommended.Type = 'TOPICAL'\n    ORDER BY CHECKSUM(NEWID())\n) AS Recommended ON Items.ProductCode = Recommended.ProductCode\nINNER JOIN tblBooks ON Items.BookId = tblBooks.BookId; -- Assuming there's a join condition between Items and tblBooks