-- Begin the transaction\nBEGIN;\n\n-- Update statement with CASE to switch values\nUPDATE your_table t\nSET priority = CASE \n                 WHEN t.priority = 1 THEN 5\n                 WHEN t.priority = 2 THEN 4\n                 WHEN t.priority = 3 THEN 3\n                 WHEN t.priority = 4 THEN 2\n                 WHEN t.priority = 5 THEN 1\n               END;\n\n-- Commit the transaction if all is well\nCOMMIT;