-- Example: Create a temporary table to hold distinct values\nCREATE TABLE TempUnique (\n    field1Date DATE,\n    field2Number INTEGER,\n    field3Text TEXT\n);\n\n-- Insert distinct records from the original table to the temporary table\nINSERT INTO TempUnique (field1Date, field2Number, field3Text)\nSELECT DISTINCT tablename.field1Date, tablename.field2Number, tablename.field3Text\nFROM tablename;