-- Create table if not exists\nCREATE TABLE faq (\n    id SERIAL PRIMARY KEY,\n    question TEXT NOT NULL,\n    answer TEXT NOT NULL\n);\n\n-- Add a full-text search column\nALTER TABLE faq ADD COLUMN document TSVECTOR;\n\n-- Update the tsvector column for existing data\nUPDATE faq SET document = to_tsvector('english', question || ' ' || answer);\n\n-- Create an index on the tsvector column\nCREATE INDEX idx_document ON faq USING GIN (document);