DECLARE\n  v_row_id number;\nBEGIN\n  FOR rec IN (SELECT * FROM your_source_table) LOOP\n    BEGIN\n      -- Use explicit column list in INSERT for clarity and safety.\n      INSERT INTO target_table (column1, column2, ...)\n      VALUES (rec.column1, rec.column2, ...);\n      \n      COMMIT; -- Commit each successful row if needed\n      \n    EXCEPTION \n      WHEN OTHERS THEN\n        -- Log the problematic row details\n        DBMS_OUTPUT.PUT_LINE('Error at row ' || v_row_id || ': ' || SQLERRM);\n        \n        -- Optionally, log to an error table for further analysis\n        INSERT INTO error_log_table (row_id, error_message)\n        VALUES (v_row_id, SQLERRM);\n        \n        COMMIT; -- Commit the logging transaction\n        \n        -- Continue with next row\n    END;\n  END LOOP;\nEND;\n/