SELECT \n    mov_id, \n    mov_title,\n    GROUP_CONCAT(DISTINCT categories.cat_name ORDER BY cat_name SEPARATOR ', ') as all_genres, \n    GROUP_CONCAT(DISTINCT cat_id ORDER BY cat_id SEPARATOR ',') as all_genres_ids,  \n    GROUP_CONCAT(CASE WHEN gen_dominant = 1 THEN CAST(gen_catid AS CHAR) ELSE '0' END\n                 ORDER BY CASE \n                            WHEN gen_dominant = 1 THEN 0\n                            ELSE 1\n                          END,\n                          gen_catid SEPARATOR ',') as dominant_genre_ids, \n    GROUP_CONCAT(DISTINCT actors.act_name ORDER BY act_name SEPARATOR ', ') as all_actors, \n    GROUP_CONCAT(DISTINCT CAST(actors.act_id AS CHAR) ORDER BY actors.act_id SEPARATOR ',') as all_actor_ids, \n    mov_desc, \n    mov_added, \n    mov_thumb, \n    mov_hits, \n    mov_numvotes, \n    mov_totalvote, \n    mov_imdb, \n    mov_release, \n    mov_html, \n    mov_type,\n    mov_buytickets,\n    ep_summary,\n    ep_airdate,\n    ep_id,\n    ep_hits,\n    ep_totalNs,\n    ep_totalRs,\n    mov_rating,\n    mov_rating_reason,\n    mrate_name,\n    dir_id, \n    dir_name\nFROM movies\nJOIN _genres ON movies.mov_id = _genres.gen_movieid\nJOIN categories ON _genres.gen_catid = categories.cat_id\nLEFT JOIN _actors ON (movies.mov_id = _actors.ac_movid)\nLEFT JOIN actors ON (_actors.ac_actorid = actors.act_id AND act_famous = 1)\nJOIN directors ON movies.mov_director = directors.dir_id\nJOIN movie_ratings ON movies.mov_rating = movie_ratings.mrate_id\nLEFT JOIN episodes ON mov_id = ep_showid AND ep_season = 0 AND ep_num = 0\nWHERE mov_id = *MOVIE_ID* AND mov_status = 1 \nGROUP BY mov_id;