-- Disable all ON DELETE CASCADE temporarily\nDECLARE @sql NVARCHAR(MAX) = N'';\n\nSELECT @sql += 'ALTER TABLE [' + SCHEMA_NAME(t.schema_id) + '].[' + t.name + '] NOCHECK CONSTRAINT [' + c.name + '];'\nFROM sys.tables AS t\nINNER JOIN sys.foreign_keys AS fk ON t.object_id = fk.parent_object_id\nINNER JOIN sys.foreign_key_columns AS fkc ON fk.object_id = fkc.constraint_object_id\nINNER JOIN sys.columns AS c ON fkc.constraint_column_id = c.column_id AND fkc.parent_object_id = c.object_id\nWHERE fk.delete_referential_action_desc = 'CASCADE';\n\nEXEC sp_executesql @sql;\n\n-- Enable all ON DELETE CASCADE temporarily\nSET @sql = N'';\n\nSELECT @sql += 'ALTER TABLE [' + SCHEMA_NAME(t.schema_id) + '].[' + t.name + '] WITH CHECK CHECK CONSTRAINT [' + c.name + '];'\nFROM sys.tables AS t\nINNER JOIN sys.foreign_keys AS fk ON t.object_id = fk.parent_object_id\nINNER JOIN sys.foreign_key_columns AS fkc ON fk.object_id = fkc.constraint_object_id\nINNER JOIN sys.columns AS c ON fkc.constraint_column_id = c.column_id AND fkc.parent_object_id = c.object_id\nWHERE fk.delete_referential_action_desc = 'CASCADE';\n\nEXEC sp_executesql @sql;\n\n-- Re-enable constraints with CASCADE temporarily and perform delete\nSET @sql = N'';\n\nSELECT @sql += 'ALTER TABLE [' + SCHEMA_NAME(t.schema_id) + '].[' + t.name + '] WITH CHECK ADD CONSTRAINT [' + c.name + '] FOREIGN KEY([' + COL_NAME(fkc.parent_object_id, fkc.parent_column_id) + ']) REFERENCES [' + OBJECT_NAME(fkc.referenced_object_id)+'] (['+COL_NAME (fkc.referenced_object_id,fkc.referenced_column_id)+']) ON DELETE CASCADE;'\nFROM sys.tables AS t\nINNER JOIN sys.foreign_keys AS fk ON t.object_id = fk.parent_object_id\nINNER JOIN sys.foreign_key_columns AS fkc ON fk.object_id = fkc.constraint_object_id\nINNER JOIN sys.columns AS c ON fkc.constraint_column_id = c.column_id AND fkc.parent_object_id = c.object_id\nWHERE fk.delete_referential_action_desc <> 'CASCADE';\n\nEXEC sp_executesql @sql;\n\n-- Perform your delete operation here\nDELETE FROM Table1 WHERE SomeCondition;\n\n-- Reset constraints back to original state\nDECLARE @disableSql NVARCHAR(MAX) = N'';\nDECLARE @enableCheckSql NVARCHAR(MAX) = N'';\n\nSELECT \n    @disableSql += 'ALTER TABLE [' + SCHEMA_NAME(t.schema_id) + '].[' + t.name + '] NOCHECK CONSTRAINT [' + c.name + '];',\n    @enableCheckSql += 'ALTER TABLE [' + SCHEMA_NAME(t.schema_id) + '].[' + t.name + '] WITH CHECK CHECK CONSTRAINT [' + c.name + '];'\nFROM sys.tables AS t\nINNER JOIN sys.foreign_keys AS fk ON t.object_id = fk.parent_object_id\nINNER JOIN sys.foreign_key_columns AS fkc ON fk.object_id = fkc.constraint_object_id\nINNER JOIN sys.columns AS c ON fkc.constraint_column_id = c.column_id AND fkc.parent_object_id = c.object_id;\n\nEXEC sp_executesql @disableSql;\nEXEC sp_executesql @enableCheckSql;