SELECT \n    *\nFROM\n    o1,\n    o2\nWHERE\n    o1.from < (o2.to - NUMTODSINTERVAL(\n        (EXTRACT(DAY FROM (o2.to - o2.from)) * 86400 +\n         EXTRACT(HOUR FROM (o2.to - o2.from)) * 3600 +\n         EXTRACT(MINUTE FROM (o2.to - o2.from)) * 60 +\n         EXTRACT(SECOND FROM (o2.to - o2.from))) / 2, 'SECOND'))\n    AND\n    o1.to > (o2.to - NUMTODSINTERVAL(\n        (EXTRACT(DAY FROM (o2.to - o2.from)) * 86400 +\n         EXTRACT(HOUR FROM (o2.to - o2.from)) * 3600 +\n         EXTRACT(MINUTE FROM (o2.to - o2.from)) * 60 +\n         EXTRACT(SECOND FROM (o2.to - o2.from))) / 2, 'SECOND'));