CREATE TABLE People (\n    PersonId INT IDENTITY(1,1) PRIMARY KEY,\n    Name NVARCHAR(255),\n    SSN NVARCHAR(11)\n);\n\nCREATE TABLE Loans (\n    LoanId INT IDENTITY(1,1) PRIMARY KEY,\n    Amount DECIMAL(18, 2)\n);\n\n-- New table to hold borrower groups with a name/description\nCREATE TABLE BorrowerGroups (\n    GroupId INT IDENTITY(1,1) PRIMARY KEY,\n    Name NVARCHAR(255) -- Descriptive name for the group e.g., "Mr. Smith and Wife"\n);\n\n-- Table linking people to borrower groups\nCREATE TABLE PeopleInBorrowerGroup (\n    PersonGroupId INT IDENTITY(1,1) PRIMARY KEY,\n    GroupId INT FOREIGN KEY REFERENCES BorrowerGroups(GroupId),\n    PersonId INT FOREIGN KEY REFERENCES People(PersonId)\n);\n\n-- Table linking loans to borrower groups (since the group is treated as an individual entity for your application's purposes)\nCREATE TABLE LoansInBorrowerGroup (\n    LoanInGroupId INT IDENTITY(1,1) PRIMARY KEY,\n    GroupId INT FOREIGN KEY REFERENCES BorrowerGroups(GroupId),\n    LoanId INT FOREIGN KEY REFERENCES Loans(LoanId)\n);