CREATE TABLE complex_key_example (\n    prefix_part CHAR(3) NOT NULL CHECK (prefix_part ~* '^a\d{2}$'),  -- Ensures the format "a00"\n    unique_id CHAR(3) NOT NULL,                                     -- Ensures three digits with leading zeros\n    year_part INT NOT NULL,\n    PRIMARY KEY (prefix_part, unique_id, year_part)\n);\n\nCREATE VIEW complex_key_view AS\nSELECT \n    prefix_part || '-' || LPAD(unique_id::TEXT, 3, '0') || '-' || year_part::TEXT AS complex_key,\n    *\nFROM complex_key_example;\n\n-- Example of inserting data\nINSERT INTO complex_key_example (prefix_part, unique_id, year_part) VALUES ('a00', '014', 2008);