SELECT \n    Fish.ID,\n    Fish.Name,\n    Properties.Length,\n    Properties.Girth,\n    Properties.Weight\nFROM \n    Fish\nLEFT JOIN (\n    SELECT \n        FishID,\n        MAX(CASE WHEN Property = 'Length' THEN Value END) AS Length,\n        MAX(CASE WHEN Property = 'Girth' THEN Value END) AS Girth,\n        MAX(CASE WHEN Property = 'Weight' THEN Value END) AS Weight\n    FROM \n        FishProperty\n    GROUP BY \n        FishID\n) AS Properties ON Fish.ID = Properties.FishID;