CREATE OR REPLACE FUNCTION update_last_modified()\n   RETURNS TRIGGER AS $$\n   BEGIN\n       UPDATE table_update_log\n       SET last_updated = CURRENT_TIMESTAMP\n       WHERE schema_name = TG_TABLE_SCHEMA AND table_name = TG_TABLE_NAME;\n       \n       IF NOT FOUND THEN\n           INSERT INTO table_update_log (schema_name, table_name, last_updated)\n           VALUES (TG_TABLE_SCHEMA, TG_TABLE_NAME, CURRENT_TIMESTAMP);\n       END IF;\n\n       RETURN NEW;\n   END;\n   $$ LANGUAGE plpgsql;\n\n   CREATE TRIGGER trigger_after_update AFTER UPDATE ON your_table FOR EACH ROW EXECUTE FUNCTION update_last_modified();