-- Create a virtual column to improve indexing\nALTER TABLE your_table_name ADD (key2_column GENERATED ALWAYS AS (SUBSTR(your_text_column, INSTR(your_text_column, 'key2=') + 5, \nCASE WHEN INSTR(your_text_column, ',', INSTR(your_text_column, 'key2=')) > 0 \n     THEN INSTR(your_text_column, ',', INSTR(your_text_column, 'key2=')) - INSTR(your_text_column, 'key2=') - 5\n     ELSE LENGTH(your_text_column) - INSTR(your_text_column, 'key2=') + 1 END));\n\n-- Create an index on the virtual column\nCREATE INDEX idx_key2 ON your_table_name(key2_column);