WITH NewValueIds AS (\n    SELECT DISTINCT AssessmentId, v.ValueId\n    FROM (\n        SELECT 710 + n - 1 AS ValueId\n        FROM (SELECT level AS n FROM dual CONNECT BY LEVEL <= 24) -- Adjust 24 to the number of new ValueIds you need\n    ) v,\n    (SELECT DISTINCT AssessmentId FROM your_table_name) a -- Replace `your_table_name` with your actual table name\n)\nINSERT INTO your_table_name (AssessmentId, ValueId, Value)\nSELECT AssessmentId, ValueId, NULL AS Value\nFROM NewValueIds;