WITH ToDelete AS (\n    SELECT t1.Id, t2.Id, t3.Id  -- Specify the primary keys of tables you want to delete from\n    FROM table1 t1\n    INNER JOIN table2 t2 ON t1.SomeColumn = t2.AnotherColumn\n    INNER JOIN table3 t3 ON t2.YetAnotherColumn = t3.SomeOtherColumn\n)\nDELETE FROM table1 WHERE Id IN (SELECT Id FROM ToDelete);\nDELETE FROM table2 WHERE Id IN (SELECT Id FROM ToDelete);\nDELETE FROM table3 WHERE Id IN (SELECT Id FROM ToDelete);