CREATE TABLE SearchStatistics (\n    SearchID INT AUTO_INCREMENT PRIMARY KEY,\n    UserID INT NULL,  -- Null if the user is not logged in\n    SearchTerm VARCHAR(255) NOT NULL,\n    SearchType ENUM('company_name', 'area_code', 'services', ...) NOT NULL,\n    Timestamp DATETIME DEFAULT CURRENT_TIMESTAMP,\n    IPAddress VARBINARY(16),  -- Use INET6_ATON for IPv4 or store as varbinary(16) to accommodate both IPv4 and IPv6\n    SessionID CHAR(32),      -- To track multiple searches by the same session\n    ResultsCount INT          -- Number of results returned for this search\n);