CREATE TABLE inventorycontrol (\n     ID                         INT NOT NULL UNIQUE AUTO_INCREMENT,\n    ,nodeID                     INT NOT NULL CHECK (nodeID > 0)\n    ,custom1                    VARCHAR(128) NOT NULL\n    ,custom2                    VARCHAR(128) NOT NULL\n    ,custom3                    VARCHAR(128) NOT NULL\n    ,custom4                    VARCHAR(128) NOT NULL\n    ,articlekey                 VARCHAR(32) NOT NULL \n    ,amount                     INT NOT NULL CHECK (amount > 0)\n    ,orderID                    INT NULL\n    ,supplierID                 INT NULL\n    ,customerID                 INT NULL\n    ,datecreated                DATETIME NOT NULL\n    ,expectedcompletion         DATETIME NOT NULL\n    ,datecompleted              DATETIME NULL\n\n    ,PRIMARY KEY (articlekey, datecreated)\n);