CREATE TABLE GeographyTable (\n    GeogId INT IDENTITY(1,1) PRIMARY KEY,\n    Location GEOMETRY,\n);\n\n-- Insert a point into the table\nINSERT INTO GeographyTable (Location)\nVALUES (geometry::STGeomFromText('POINT(-73.985608 40.748817)', 4326));\n\n-- Query to find spatial data within a certain distance\nSELECT Location \nFROM GeographyTable \nWHERE Location.STDistance(geometry::STGeomFromText('POINT(-74.014797 40.706544)', 4326)) < 5000;