SELECT \n    i.itemID, \n    COUNT(v.itemID) AS votes,\n    i.title, \n    i.itemTypeID, \n    i.submitDate, \n    i.deleted, \n    i.ItemCat,\n    i.counter, \n    i.userID, \n    u.name,\n    TIMESTAMPDIFF(MINUTE, i.submitDate, NOW()) AS timeMin,\n    mi.userID AS userIDFav, \n    mi.deleted AS myDeleted\nFROM \n    item i\nLEFT JOIN \n    votes v ON i.itemID = v.itemID\nJOIN \n    users u ON i.userID = u.userID\nLEFT JOIN \n    myItems mi ON i.itemID = mi.itemID AND (mi.userID = 3 OR mi.userID IS NULL)\nWHERE \n    i.deleted = 0\nGROUP BY \n    i.itemID, \n    i.title, \n    i.itemTypeID, \n    i.submitDate, \n    i.ItemCat,\n    i.counter, \n    i.userID, \n    u.name,\n    mi.userID, \n    mi.deleted\nORDER BY \n    i.itemID DESC\nLIMIT 0, 20;