-- Main Ship Table with static details\nCREATE TABLE Ship (\n    ID smallint IDENTITY(1,1) PRIMARY KEY,\n    IMO int NOT NULL UNIQUE,\n    -- Other static fields can be added here\n);\n\n-- Dynamic Details using JSON or XML (based on the DBMS support)\nCREATE TABLE ShipDetails (\n    Ship_ID smallint FOREIGN KEY REFERENCES Ship(ID),\n    Details jsonb,  -- Using PostgreSQL's jsonb data type for dynamic attributes\n    ModifiedDate smalldatetime,\n    User_ID smallint NOT NULL -- Assuming you have a user table\n);