WITH BaseData AS (\n    SELECT TOP 10 -- or your desired row count\n        Column1, \n        Column2, \n        Column3 \n    FROM \n        YourTable \n    ORDER BY \n        SomeColumn\n)\nSELECT * \nFROM BaseData\n\nUNION ALL \n\nSELECT \n    'Default1' AS Column1, \n    'Default2' AS Column2, \n    'Default3' AS Column3 \nWHERE (SELECT COUNT(*) FROM BaseData) < 10 -- Ensure we add rows if less than 10