WITH RandomizedColumns AS (\n    SELECT \n        c1.value AS col1,\n        c2.value AS col2,\n        c3.value AS col3,\n        c4.value AS col4,\n        c5.value AS col5\n    FROM your_table t\n    CROSS APPLY (SELECT TOP 1 col1 FROM your_table ORDER BY NEWID()) c1(value)\n    CROSS APPLY (SELECT TOP 1 col2 FROM your_table ORDER BY NEWID()) c2(value)\n    CROSS APPLY (SELECT TOP 1 col3 FROM your_table ORDER BY NEWID()) c3(value)\n    CROSS APPLY (SELECT TOP 1 col4 FROM your_table ORDER BY NEWID()) c4(value)\n    CROSS APPLY (SELECT TOP 1 col5 FROM your_table ORDER BY NEWID()) c5(value)\n)\nSELECT * \nFROM RandomizedColumns;