-- At the Publisher, in the master database, run the following command:\nEXEC sp_replicationdboption\n    @dbname = N'YourPublisherDbName',\n    @optname = N'merge publish',\n    @value = N'true';\n\n-- Add a new publication.\nUSE [YourPublisherDbName]\nGO\nEXEC sp_addmergepublication \n    @publication = N'MyMergePublication', \n    @description = N'Merge publication of database ''YourPublisherDbName'' from Publisher ''YourPublisherInstance''.',\n    @retention = 48,\n    @sync_mode = N'native';\n\n-- Add an article.\nUSE [YourPublisherDbName]\nGO\nEXEC sp_addmergearticle \n    @publication = N'MyMergePublication', \n    @article = N'YourTableName', \n    @source_owner = N'dbo', \n    @source_object = N'YourTableName', \n    @type = N'table', \n    @description = N'', \n    @column_tracking = N'false';