-- Users Table\n   CREATE TABLE users (\n       uid INT(5) AUTO_INCREMENT PRIMARY KEY,\n       username VARCHAR(128) NOT NULL UNIQUE\n   );\n\n   -- Items Table\n   CREATE TABLE items (\n       item_id INT(5) AUTO_INCREMENT PRIMARY KEY,\n       item_name VARCHAR(128) NOT NULL\n   );\n\n   -- UserItems (Association table to link users with items)\n   CREATE TABLE user_items (\n       uid INT(5),\n       item_id INT(5),\n       item_hash CHAR(40),  -- Hash of the item_id + salt or other unique identifier\n       PRIMARY KEY (uid, item_id),\n       FOREIGN KEY (uid) REFERENCES users(uid),\n       FOREIGN KEY (item_id) REFERENCES items(item_id)\n   );