-- Step 1: Create a login\nUSE master;\nGO\nCREATE LOGIN [LimitedUser] WITH PASSWORD = 'StrongPassword!';\nGO\n\n-- Step 2: Add user to the target database\nUSE YourDatabaseName; -- Replace with your database name\nGO\nCREATE USER [LimitedUser] FOR LOGIN [LimitedUser];\nGO\n\n-- Step 3: Grant SELECT permission on specific views\nGRANT SELECT ON [dbo].[YourView1] TO [LimitedUser]; -- Replace with your view name\nGRANT SELECT ON [dbo].[YourView2] TO [LimitedUser]; -- Repeat for additional views as needed\nGO