-- Example of a simple anonymous block\nBEGIN\n  -- Declaration section\n  DECLARE\n    v_employee_id employees.employee_id%TYPE;\n    \n  -- Execution section\n  BEGIN\n    SELECT employee_id INTO v_employee_id FROM employees WHERE first_name = 'John' AND last_name = 'Doe';\n    IF v_employee_id IS NOT NULL THEN\n      DBMS_OUTPUT.PUT_LINE('Employee ID is: ' || v_employee_id);\n    ELSE\n      DBMS_OUTPUT.PUT_LINE('No employee found.');\n    END IF;\n  EXCEPTION\n    -- Exception handling section\n    WHEN NO_DATA_FOUND THEN\n      DBMS_OUTPUT.PUT_LINE('No data found exception occurred.');\n    WHEN OTHERS THEN\n      DBMS_OUTPUT.PUT_LINE('An unexpected error occurred: ' || SQLERRM);\n  END;\nEND;