SELECT s1.username || '@' || s1.machine || ' ( SID=' || s1.sid || ' )  is blocking '\n     || s2.username || '@' || s2.machine || ' ( SID=' || s2.sid || ' ) ' AS blocking_information\nFROM v$lock l1\nJOIN v$session s1 ON l1.lmode != 0 AND l1.sid = s1.sid\nJOIN v$lock l2 ON l2.request > 0 AND l2.id1 = l1.id1 AND l2.id2 = l2.id2\nJOIN v$session s2 ON s2.sid = l2.sid\nORDER BY l2.request;