DECLARE @SearchStr nvarchar(100)\nDECLARE @TableName sysname\nDECLARE @ColumnName sysname\nDECLARE @FullCommand nvarchar(max)\n\n-- Set the string you want to search for here\nSET @SearchStr = N'YourSearchString'\n\nDECLARE Table_Cursor CURSOR FOR\n    SELECT TABLE_SCHEMA + '.' + TABLE_NAME \n    FROM INFORMATION_SCHEMA.TABLES\n    WHERE TABLE_TYPE = 'BASE TABLE' AND TABLE_CATALOG = DB_NAME()\n\nOPEN Table_Cursor\n\nFETCH NEXT FROM Table_Cursor INTO @TableName\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    DECLARE Column_Cursor CURSOR FOR\n        SELECT COLUMN_NAME\n        FROM INFORMATION_SCHEMA.COLUMNS\n        WHERE DATA_TYPE IN ('char', 'varchar', 'nchar', 'nvarchar', 'text', 'ntext')\n          AND TABLE_SCHEMA + '.' + TABLE_NAME = @TableName\n\n    OPEN Column_Cursor\n\n    FETCH NEXT FROM Column_Cursor INTO @ColumnName\n\n    WHILE @@FETCH_STATUS = 0\n    BEGIN\n        SET @FullCommand = \n            N'SELECT ''' + @TableName + ''' AS TableName, [' + @ColumnName + '] \n             FROM ' + QUOTENAME(@TableName) +\n             ' WHERE [' + @ColumnName + '] LIKE ''%' + REPLACE(REPLACE(@SearchStr, '[', '[[]'), '%', '[%]') + '%'''\n\n        EXEC sp_executesql @FullCommand\n\n        FETCH NEXT FROM Column_Cursor INTO @ColumnName\n    END\n\n    CLOSE Column_Cursor\n    DEALLOCATE Column_Cursor\n\n    FETCH NEXT FROM Table_Cursor INTO @TableName\nEND\n\nCLOSE Table_Cursor\nDEALLOCATE Table_Cursor