CREATE TABLE Building (\n    id INT PRIMARY KEY AUTO_INCREMENT,\n    location VARCHAR(255),\n    name VARCHAR(255)\n);\n\nCREATE TABLE House (\n    id INT PRIMARY KEY,\n    FOREIGN KEY (id) REFERENCES Building(id),\n    num_rooms INT,\n    num_bathrooms INT\n);\n\nCREATE TABLE Office (\n    id INT PRIMARY KEY,\n    FOREIGN KEY (id) REFERENCES Building(id),\n    offices INT,\n    sqfoot DECIMAL(10, 2)\n);