INSERT INTO Sales (SaleID, ProductID, SaleDate, Amount)\nSELECT \n    s.SaleID + (YEAR(s.SaleDate) - 2023) * 1000000 + (i-1) * 100000, -- Adjusting SaleID to prevent duplicates\n    s.ProductID,\n    DATEADD(YEAR, i-1, s.SaleDate), -- Shifting the sale date by 1,2,...9 years further\n    s.Amount\nFROM Sales s\nCROSS JOIN (SELECT TOP 10 ROW_NUMBER() OVER(ORDER BY (SELECT NULL)) AS i FROM sys.columns) t\nWHERE YEAR(s.SaleDate) = 2023; -- Assuming the original data is from the year 2023