CREATE OR REPLACE FUNCTION get_internal_point(sdo_geometry IN MDSYS.SDO_GEOMETRY)\nRETURN MDSYS.SDO_GEOMETRY IS\n    internal_polygon MDSYS.SDO_GEOMETRY;\nBEGIN\n    -- Create an inner polygon by buffering in with a small negative distance.\n    internal_polygon := SDO_UTIL.apex(sdo_geometry, -0.1);\n    \n    -- Return a point on the surface of this inner polygon.\n    RETURN sdo_geom.sdo_pointonsurface(internal_polygon, 0.05);\nEND;\n/