-- Assuming @friendId is the ID of the friend to be moved and @userId is the user's ID\n     DECLARE @currentFirstFriend int;\n     SELECT @currentFirstFriend = NextFriendId FROM Friends WHERE UserId = @userId AND PreviousFriendId IS NULL;\n\n     UPDATE Friends SET NextFriendId = @friendId WHERE FriendId = @currentFirstFriend;\n     UPDATE Friends SET PreviousFriendId = @currentFirstFriend, NextFriendId = NULL, PreviousFriendId = NULL\n         WHERE FriendId = @friendId;