-- Creating a standard heap table (similar to MyISAM behavior)\nCREATE TABLE my_table (\n    id NUMBER PRIMARY KEY,\n    name VARCHAR2(100)\n);\n\n-- Creating an archived partitioned table\nCREATE TABLE archived_data (\n    data_id NUMBER PRIMARY KEY,\n    content CLOB,\n    archive_date DATE\n) PARTITION BY RANGE (archive_date) (\n    PARTITION p_before_2023 VALUES LESS THAN (TO_DATE('2023-01-01', 'YYYY-MM-DD')),\n    PARTITION p_after_2023 VALUES LESS THAN (MAXVALUE)\n);\n\n-- Example of a "blackhole" like insertion procedure\nCREATE OR REPLACE PROCEDURE insert_ignore(data IN VARCHAR2) IS\nBEGIN\n    -- Optionally log the ignored data or perform some other operation\n    NULL; -- This effectively ignores the input\nEND;