WITH GroupSequenceCheck AS (\n    SELECT \n        "Group",\n        MAX(CASE WHEN ROW_NUMBER() OVER (PARTITION BY "Group" ORDER BY "Sequence") != ("Sequence" - MIN("Sequence") OVER (PARTITION BY "Group")) THEN 1 ELSE 0 END) AS HasNonContinuousSequence\n    FROM \n        YourTableName\n    GROUP BY \n        "Group"\n)\nSELECT \n    "Group"\nFROM \n    GroupSequenceCheck\nWHERE \n    HasNonContinuousSequence = 1;